import React, { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import { useUserContext } from "../contexts/UserContext"

interface UserProfileProps {
  id: number
  name: string
  email: string
}

export const UserProfile: React.FC = () => {
  const { id } = useParams<{ id: string }>()
  const { users } = useUserContext()
  const [userProfile, setUserProfile] = useState<UserProfileProps | null>(null)

  useEffect(() => {
    // Find user from context
    const user = users.find((user) => user.id.toString() === id)
    if (user) {
      setUserProfile(user)
    } else {
      // Fallback: fetch from API if not found in context
      fetch(`https://jsonplaceholder.typicode.com/users/${id}`)
        .then((response) => response.json())
        .then((data) => setUserProfile(data))
        .catch((error) => console.error("Error fetching user profile:", error))
    }
  }, [id, users])

  if (!userProfile) return <div>Loading...</div>

  return (
    <div>
      <h1>{userProfile.name}'s Profile</h1>
      <p>Email: {userProfile.email}</p>
      {/* Display more user details as needed */}
    </div>
  )
}
